mapper.select:
SELECT user FROM users
WHERE email = #{email} AND answer =
#{answer} AND question = #{question}

SELECT user_id FROM users
WHERE email = #{email}
AND answer =
#{answer}
AND user = #{user}
AND question = #{question}

SELECT user_id, answer FROM users
WHERE email = #{email}
AND user = #{user}
AND question = #{question}

mapper.update:
UPDATE users SET
password = #{password}
WHERE user = #{user}
